const contextReducer = (state, action) =>{
  switch (action.type){
    case 'Delete':
      const delItem = state.filter((t)=>t.id !== action.payload)
      return delItem;
    case 'Add':
      const addItem = [action.payload, ...state]
      return addItem;
    default:
      return state;
  }
}

export default contextReducer
