import { Card } from 'antd'
import React from 'react'
import FormOpt from './Form/Form';
import ListTracker from './List/List';


const Main = ({title}) => {
  return (
    <div>
      <Card headStyle={{fontWeight:'bold'}} title={title} bordered={true} style={{boxShadow:'rgba(0, 0, 0, 0.2) 2px 2px 6px'}}>
        <h2>總收支:</h2>
        <FormOpt />
        <ListTracker />
      </Card>
    </div>
  )
}

export default Main
