import React, {useContext} from 'react'
import { List } from 'antd';
import { DollarCircleOutlined, DeleteOutlined } from '@ant-design/icons';
import styled from "@emotion/styled";
import InfiniteScroll from 'react-infinite-scroller';
import {ExpenseTrackerContext} from '../../../context/context'

const ListContainer = styled.div`
  border: 1px solid #e8e8e8;
  border-radius: 4px;
  overflow: auto;
  padding: 8px 24px;
  height: 200px;
`

const ListTracker = () => {
  const globalState = useContext(ExpenseTrackerContext)
  console.log(globalState)
  const transcations = [
    {id:1, type:'income', categorty:'薪水', date:'2021/01/01', amount:1000},
    {id:2, type:'expense', categorty:'午餐', date:'2021/01/01', amount:80},
    {id:3, type:'expense', categorty:'晚餐', date:'2021/01/01', amount:100},
  ]

  return (
    <ListContainer>
      <InfiniteScroll
          initialLoad={false}
          pageStart={0}
          useWindow={false}
          loadMore={''}
        >
          <List
            dataSource={transcations}
            renderItem={item => (
              <List.Item key={item.id}>
                <List.Item.Meta
                  avatar={ item.type === 'income' ? 
                    (<DollarCircleOutlined style={{color:'green',fontSize:'24px'}}/>) 
                    : (<DollarCircleOutlined style={{color:'red',fontSize:'24px'}}/>)
                  }
                  title={<div>${item.amount}</div>}
                  description={<div>{item.categorty} - {item.date}</div>}
                />
                <DeleteOutlined style={{fontSize:'24px'}} />
              </List.Item>
            )}
          >
          </List>
        </InfiniteScroll>
    </ListContainer>
  )
}

export default ListTracker
