import './App.css';
import 'antd/dist/antd.css'
import { Col, Row } from 'antd'
import Details from './components/Details/Details';
import Main from './components/Main/Main'
import styled from "@emotion/styled";
import { Provider } from "./context/context";

const Wrapper = styled.div`
  width:100%;
  max-width:1280px;
  height:100%;
  margin:0 auto;
`
const Box = styled.div`
  margin: 0 16px;
`

function App() {
  return (
    <Provider>
      <Wrapper>
        <Box>
          <Row gutter={[16,32]}>
            <Col xs={24} md={12}>
              <Main title={'記帳本'} />
            </Col>
            <Col xs={24} md={12}  style={{marginBottom:'32px'}}>
              <Details title={'支出'} bgc='tomato' color='Brown' />
              <Details title={'收入'} bgc='yellowgreen' color='darkgreen' />
            </Col>
          </Row>
        </Box>
      </Wrapper>
    </Provider>
  );
}

export default App;
