import React, { useReducer, createContext } from 'react'
import contextReducer from './contextReducer'

const initialState = []
export const ExpenseTrackerContext = createContext(initialState)

export const Provider = ({children}) =>{
  const [transactions, dispatch] = useReducer(contextReducer, initialState)
  
  // action creators
  const deleteTransaction = (id) => dispatch({type:'Delete', payload:id})
  const addTransaction = (transaction) => dispatch({type:'Add', payload:transaction})
  
  console.log('transaction',transactions)

  const balance = transactions.reduce((acc,currVal)=> transactions.type === '收入' ? acc += currVal.amount : acc -= currVal.amount)

  return(
    <ExpenseTrackerContext.Provider value={{
      deleteTransaction,
      addTransaction,
      transactions,
      balance
    }}>
      {children}
    </ExpenseTrackerContext.Provider>
  )
}
