import React from 'react'
import { Card } from 'antd'
import { Doughnut } from 'react-chartjs-2';
import useTransaction from "../../useTransactionAPI/useTransaction";

const Details = ({title,bgc,color,marginBot}) => {
  const {total, chartData} = useTransaction({title})
  return (
    <Card title={title} headStyle={{color:color,backgroundColor:bgc,fontWeight:'bold'}} bordered={true} style={{boxShadow:'rgba(0, 0, 0, 0.2) 2px 2px 6px',marginBottom:marginBot}}>
      <p>${total}</p>
      <Doughnut data={chartData} />
    </Card>
  )
}

export default Details
