import React from 'react'
import { List, message, Avatar, Spin } from 'antd';
import styled from "@emotion/styled";
import InfiniteScroll from 'react-infinite-scroller';

const ListContainer = styled.div`
  border: 1px solid #e8e8e8;
  border-radius: 4px;
  overflow: auto;
  padding: 8px 24px;
  height: 200px;
`
const LoadingContainer = styled.div`
  position: absolute;
  bottom: 40px;
  width: 100%;
  text-align: center;
`


const ListTracker = () => {

  const transcations = [
    {id:1, type:'Income', categorty:'breakfast', date:new Date()},
    {id:2, type:'Income', categorty:'breakfast', date:new Date()},
    {id:3, type:'Income', categorty:'breakfast', date:new Date()}
  ]

  return (
    <ListContainer>
      <InfiniteScroll
          initialLoad={false}
          pageStart={0}
          useWindow={false}
        >
          <List
            dataSource={transcations}
            renderItem={item => (
              <List.Item key={transcations.id}>
                <List.Item.Meta
                  avatar={
                    <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                  }
                  title={<div>{transcations.type}</div>}
                  description={transcations.categorty}
                />
                <div>Content</div>
              </List.Item>
            )}
          >
            {/* {this.state.loading && this.state.hasMore && (
              <LoadingContainer>
                <Spin />
              </LoadingContainer>
            )} */}
          </List>
        </InfiniteScroll>
    </ListContainer>
  )
}

export default ListTracker
