const contextReducer = (state, action) =>{
  switch (action.type){
    case 'Delete':
      const delItem = state.filter((t)=>t.id !== action.payload)
      return delItem;
    case 'Add':
      const addItem = [...state, action.payload]
      return addItem
  }
}

export default contextReducer
